import React, { Children } from "react";
import styles from "./HomePage.module.css";
import {
  Header,
  Footer,
  Carousel,
  SideMenu,
  ProductCollection,
  FeaturedClients,
} from "../../components";
import { Row, Col, Typography, Spin } from "antd";
import { MainLayout } from "../../layouts/mainLayout";

import sideImgae1 from "../../assets/image/sider_11.png";
import sideImgae2 from "../../assets/image/sider_22.png";
import sideImgae3 from "../../assets/image/sider_33.png";
import { withTranslation, WithTranslation } from "react-i18next";
import axios from "axios";
import { connect } from "react-redux";
import { RootState } from "../../redux/store";
import {
  giveMeDataActionCreator
} from "../../redux/recommendProducts/recommendProductsActions";


const mapStateToProps = (state: RootState) => {
  return {
    loading: state.recommendProducts.loading,
    error: state.recommendProducts.error,
    productList: state.recommendProducts.productList,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    giveMeData: () => {
      dispatch(giveMeDataActionCreator());
    }
  };
};

type PropsType = WithTranslation &
  ReturnType<typeof mapStateToProps> &
  ReturnType<typeof mapDispatchToProps>;

class HomePageComponent extends React.Component<PropsType> {

  componentDidMount() {
    this.props.giveMeData();  
  }

  render() {
    // console.log(this.props.t)
    const { t, productList, loading, error  } = this.props;

    if (loading) {
      return (
        <Spin
          size="large"
          style={{
            marginTop: 200,
            marginBottom: 200,
            marginLeft: "auto",
            marginRight: "auto",
            width: "100%",
          }}
        />
      );
    }
    if (error) {
      return <div>Website Error: {error} </div>;
    }
    return (
      <MainLayout>
          <Row style={{ marginTop: 20 }}>
            <Col span={6}>
              <SideMenu />
            </Col>
            <Col span={18}>
              <Carousel />
            </Col>
          </Row>
          <ProductCollection
            title={
              <Typography.Title level={3} type="warning">
                {t("home_page.hot_recommended")}
              </Typography.Title>
            }
            sideImage={sideImgae1}
            products={productList[0].touristRoutes}
          />
          <ProductCollection
            title={
              <Typography.Title level={3} type="danger">
                {t("home_page.new_arrival")}
              </Typography.Title>
            }
            sideImage={sideImgae2}
            products={productList[1].touristRoutes}
          />
          <ProductCollection
            title={
              <Typography.Title level={3} type="success">
                {t("home_page.domestic_travel")}
              </Typography.Title>
            }
            sideImage={sideImgae3}
            products={productList[2].touristRoutes}
          />
          <FeaturedClients />
        </MainLayout>
    );
  }
}

export const HomePage = connect(
  mapStateToProps,
  mapDispatchToProps
)(withTranslation()(HomePageComponent));
