import React from "react";
import styles from "./HomePage.module.css";
import { Header, Footer, Carousel, SideMenu,ProductCollection, FeaturedClients } from "../../components";
import { Row, Col, Typography, Spin } from "antd";

import sideImgae1 from '../../assets/image/sider_11.png';
import sideImgae2 from '../../assets/image/sider_22.png';
import sideImgae3 from '../../assets/image/sider_33.png';
import { withTranslation, WithTranslation } from "react-i18next";
import axios from "axios";

interface State {
  loading: boolean,
  error: string | null,
  productList: any[]
}

class HomePageComponent extends React.Component<WithTranslation, State> {
  constructor(props){
    super(props);
    this.state = {
      loading: true,
      error: null,
      productList: [],
    }
  }  

  async componentDidMount() {
    try {
      const {data} = await axios.get(
        "http://123.56.149.216:8080/api/productCollections",
      );
      this.setState({
        loading: false,
        error: null,
        productList: data,
      });
    } catch (error) {
      if (error instanceof Error){
        this.setState({
        error: error.message,
        loading: false,
      });
      }

    }


  }

  render() {
      // console.log(this.props.t)
      const { t } = this.props; 
      const { productList, loading, error } = this.state;

      if(loading){
        return <Spin
        size="large"
        style={{
          marginTop: 200,
          marginBottom: 200,
          marginLeft: "auto",
          marginRight: "auto",
          width: "100%",
        }}
        />
      }
      if(error){
        return <div>Website Error: {error} </div>;
      }
        return <>
        <Header />
       <div className={styles['page-content']}>
        <Row style={{marginTop: 20} }>
          <Col span={6}>
            <SideMenu />
          </Col>
          <Col span={18}>
            <Carousel />
          </Col>
        </Row>
        <ProductCollection
          title={<Typography.Title level={3} type="warning">
            {t("home_page.hot_recommended")}
            </Typography.Title>}
          sideImage={sideImgae1}
          products={productList[0].touristRoutes}
        />
        <ProductCollection
          title={<Typography.Title level={3} type="danger">
            {t("home_page.new_arrival")}
            </Typography.Title>}
          sideImage={sideImgae2}
          products={productList[1].touristRoutes}
        />
        <ProductCollection
          title={<Typography.Title level={3} type="success">
            {t("home_page.domestic_travel")}
            </Typography.Title>}
          sideImage={sideImgae3}
          products={productList[2].touristRoutes}
        />

       </div>
       <FeaturedClients/>
       <Footer />
        </>;
    }
}

export const HomePage = withTranslation()(HomePageComponent)
