import React from "react";
import styles from "./Header.module.css";
import logo from '../../assets/logo.svg';
import { Layout, Typography, Input, Menu, Button, Dropdown } from "antd";
import { GlobalOutlined, FacebookOutlined, TwitterOutlined, InstagramOutlined, WhatsAppOutlined } from "@ant-design/icons";
import { withRouter, RouteComponentProps } from "react-router-dom";
import store from "../../redux/store";
import {LanguageState} from "../../redux/LanguageReducer";
import { thisExpression } from "@babel/types";
import { withTranslation, WithTranslation } from "react-i18next";

interface State extends LanguageState{}

class HeaderComponent extends React.Component<RouteComponentProps, State> {

    constructor(props){
      super(props);
      const storeState = store.getState();
      this.state = {
        language: storeState.language,
        languageList: storeState.languageList,
      };
      store.subscribe(this.handleStoreChange);
    }

    handleStoreChange = () => {
      const storeState = store.getState();
      this.setState({
        language: storeState.language,
        languageList: storeState.languageList,
      })
    }

    menuClickHandler= (e) =>{
      console.log(e);
      // this.setState({ language: e.key });
      if(e.key==="more"){
        const action = {
          type: "add_language",
          payload: { name: "new", code: "new"}
        }
        store.dispatch(action);
      }else{
        const action = {
          type: "change_language",
          payload: e.key,
        }
        store.dispatch(action);
      }
    }

    render() {
      const { history } = this.props;
      return (<div className={styles['app-header']}>
      <div className={styles['top-header']}>
        <div className={styles.inner}>
          <Typography.Text>Better your best vacation</Typography.Text>
          <Dropdown.Button
            style={{marginLeft:15}}
            overlay={
              <Menu onClick={this.menuClickHandler}>
                {this.state.languageList.map(l=>{
                  return <Menu.Item key={l.code}>{l.name}</Menu.Item>
                })}
                <Menu.Item key={"more"}>More Language Request</Menu.Item>
              </Menu>
            }
            icon={<GlobalOutlined/>}
          >{this.state.language ==="en" ? "English" : "中文"}
          </Dropdown.Button>
          <Button.Group className={styles['button-group']}>
            <Button onClick={() => history.push("register")}>Sign up</Button>
            <Button onClick={() => history.push("signIn")}>Log in</Button>
          </Button.Group>
        </div>
      </div>
      <Layout.Header className={styles['main-header']}>
        <span onClick={() => history.push("/")}>
        <img src={logo} alt="logo" className={styles['App-logo']}/>
        <Typography.Title level={3} className={styles['title']}>
          React Travel
          </Typography.Title>
        </span>
        <Input.Search 
          placeholder="Please type your dream destination"
          className={styles['search-input']}
        />
      </Layout.Header>
      <Menu mode={"horizontal"} className={styles['main-menu']}>
            <Menu.Item key={1}>Home</Menu.Item>
            <Menu.Item key={2}>Stays</Menu.Item>
            <Menu.Item key={3}>Flights</Menu.Item>
            <Menu.Item key={4}>Cars</Menu.Item>
            <Menu.Item key={5}>Packages</Menu.Item>
            <Menu.Item key={6}>Cruises</Menu.Item>
            <Menu.Item key={7}>Road Trip</Menu.Item>
            <Menu.Item key={8}>Family</Menu.Item>
            <Menu.Item key={9}>Romantic</Menu.Item>
            <Menu.Item key={10}>Solo traveler</Menu.Item>
            <Menu.Item key={11}>Beach</Menu.Item>
            <Menu.Item key={12}>Pet-friendly</Menu.Item>
            <Menu.Item key={13}>Luxury</Menu.Item>
            <Menu.Item key={14}>All-Inclusive</Menu.Item>
      </Menu>
    </div>
    )
  }  
}

export const Header = withRouter(HeaderComponent);
